def cleanup(list_of_directories):
    for idx, current_directory in enumerate(list_of_directories):
        print(idx, ". Cleaning directory:", current_directory)

        for sub_directory in os.listdir(current_directory):
            dir_path = os.path.join(current_directory, sub_directory)

            if os.path.isdir(dir_path):
                shutil.rmtree(dir_path)
            else:
                # It's a file and not a directory
                os.remove(dir_path)


cleanup(['directory1', directory2])

